const reminders = [];
const today = new Date();
today.setHours(0, 0, 0, 0); // Normalize today's date to midnight

// Calculate tomorrow's date
const tomorrow = new Date(today);
tomorrow.setDate(today.getDate() + 1);

// Access the input data safely, handling if it's a single item or an array
const inputData = Array.isArray($input.json) ? $input.json[0] : $input.json;

// Ensure the 'appointments' property exists within the input
const appointments = inputData && inputData.appointments;

if (appointments && Array.isArray(appointments)) {
    for (const item of appointments) {
        const appointmentDate = new Date(item['Appointment Date']);
        appointmentDate.setHours(0, 0, 0, 0); // Normalize appointment date to midnight

        // Check if the appointment is for tomorrow
        if (appointmentDate.getTime() === tomorrow.getTime()) {
            reminders.push({
                message: `Hi ${item['Patient Name']},\n\nThis is a reminder for your appointment tomorrow, ${item['Appointment Date']} at ${item['Appointment Time']}. We look forward to seeing you!\n\nExaCare Clinic`,
                phoneNumber: item['Phone Number'],
                patientName: item['Patient Name']
            });
        }
    }
} else {
    console.error("Code node: 'appointments' array not found or not an array in input.");
    return [{ json: { error: "'appointments' data missing or invalid" } }];
}

// Return the filtered and formatted reminders.
return [{ json: { reminders: reminders } }]; // Wrap reminders in an object for consistent output
